  if (o != null &amp;&amp; con != null) {
    try {
      pstmt = con.prepareStatement(sql);
      pstmt.setString(1, no);
      pstmt.setString(2, name);
      pstmt.setInt(3, age);
      res = pstmt.executeUpdate();
    } catch (Exception e) {
      e.printStackTrace();
    } finally {
      ConnectionUtil.closePstmt(pstmt);
      ConnectionUtil.closeConnection(con);
    }
  }

  return res;
}
