import java.sql.*;
import java.util.ArrayList;
import xust.stu.Result;
import xust.stu.ConnectionUtil;
import xust.stu.demo.domain.Student;

public class StudentDaoImpl implements StudentDao{
  //--8&lt;-- [start:init]
  public Result init() {
    Result res = new Result(false);

    Connection con = null;
    Statement stmt = null;
    String sql = "create table Student(" +
      "no TEXT primary key," +
      "name TEXT not null," +
      "gender TEXT," +
      "age INTEGER," +
      "dept TEXT" +
      ");";

    con = ConnectionUtil.getConnection();
    if(con != null){
      try {
        stmt = con.createStatement();
        ResultSet rs = stmt.executeQuery(String.format("select count(*) from sqlite_master where type = 'table' and Upper(name) = '%s'", "Student".toUpperCase()));
        while(rs.next()){
          int count = rs.getInt(1);

          if(count == 0 &amp;&amp; stmt.executeUpdate(sql) &gt; 0){
            res.code = 0;
          }          
        }
      } catch (Exception e) {
        e.printStackTrace();
        res.message = e.getMessage();
      } finally {
        ConnectionUtil.closeStmt(stmt);
        ConnectionUtil.closeConnection(con);
      }      
    }

    return res;
  }
  //--8&lt;-- [end:init]

  //--8&lt;-- [start:toR]
  /**
   * 对象填充到关系
   */
  private void toR(Student o, PreparedStatement stmt) throws Exception{
    if(o != null &amp;&amp; stmt != null){
      String no = o.getNo();
      if(no != null){
        stmt.setString(1, no);       
      }
      String name = o.getName();
      if(name != null){
        stmt.setString(2, name);       
      }
      String gender = o.getGender();
      if(gender != null){
        stmt.setString(3, gender);       
      }
      Integer age = o.getAge();
      if(age != null){
        stmt.setInt(4, age);       
      }
      String dept = o.getDept();
      if(dept != null){
        stmt.setString(5, dept);       
      }
    }
  }
  //--8&lt;-- [end:toR]

  //--8&lt;-- [start:toO]
  /**
   * 关系填充到对象
   */
  private Student toO(ResultSet rs) throws Exception{
    Student res = null;

    if(rs != null){
      res = new Student();
      res.setNo(rs.getString("no"));
      res.setName(rs.getString("name"));
      res.setGender(rs.getString("gender"));
      res.setAge(rs.getInt("age"));
      res.setDept(rs.getString("dept"));
    }

    return res;
  }
  //--8&lt;-- [end:toO]

  //--8&lt;-- [start:create]
  public Result create(Student o) {
    Result res = new Result(false);

    Connection con = null;
    PreparedStatement stmt = null;
    String sql = "insert into Student values (?, ?, ?, ?, ?)";
    con = ConnectionUtil.getConnection();
    if (o != null &amp;&amp; con != null) {
      try {
        stmt = con.prepareStatement(sql);
        toR(o, stmt);
        if(stmt.executeUpdate() &gt; 0){
          res.code = 0;
        }
      } catch (Exception e) {
        res.message = e.getMessage();
        e.printStackTrace();
      } finally {
        ConnectionUtil.closePstmt(stmt);
        ConnectionUtil.closeConnection(con);
      }
    }

    return res;
  }
  //--8&lt;-- [end:create]

  //--8&lt;-- [start:delete]
  public Result delete(String no) {
    Result res = new Result(false);

    Connection con = null;
    PreparedStatement stmt = null;
    String sql = "delete from Student where no = ?;";
    con = ConnectionUtil.getConnection();
    if(con != null){
      try {

        stmt = con.prepareStatement(sql);
        stmt.setString(1, no);
        res.code = 0;
      } catch (Exception e) {
        res.message = e.getMessage();
        e.printStackTrace();
      } finally {
        ConnectionUtil.closePstmt(stmt);
        ConnectionUtil.closeConnection(con);
      }      
    }

    return res;
  }
  //--8&lt;-- [end:delete]

  //--8&lt;-- [start:update]
  public Result update(Student o) {
    Result res = new Result(false);

    Connection con = null;
    PreparedStatement stmt = null;
    String sql = "update Student set No = ?, Name = ?, Gender = ?, Age = ?, Dept = ? where no = ?";
    con = ConnectionUtil.getConnection();
    if (o != null &amp;&amp; con != null) {
      try {

        stmt = con.prepareStatement(sql);
        toR(o, stmt);
        stmt.setString(6, o.getNo());
        if(stmt.executeUpdate() &gt; 0){
          res.code = 0;
        }
      } catch (Exception e) {
        e.printStackTrace();
        res.message = e.getMessage();
      } finally {
        ConnectionUtil.closePstmt(stmt);
        ConnectionUtil.closeConnection(con);
      }
    }

    return res;
  }
  //--8&lt;-- [end:update]

  //--8&lt;-- [start:get]
  public Result get(String no) {
    Result res = new Result(false);

    Connection con = null;
    PreparedStatement stmt = null;
    String sql = "select * from Student where no = ?";
    con = ConnectionUtil.getConnection();
    if(con != null){
      try {
        stmt = con.prepareStatement(sql);
        stmt.setString(1, no);
        ResultSet rs = stmt.executeQuery();
        Student last = null;
        while (rs.next()) {
          last = new Student();
          last.setNo(rs.getString("no"));
          last.setName(rs.getString("name"));
          last.setGender(rs.getString("gender"));
          last.setAge(rs.getInt("age"));
          last.setDept(rs.getString("dept"));
        }

        res.code = 0;
        res.data = last;
